import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, User, LogOut, Menu, X } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';

const Navbar = () => {
    const { user, role, signOut } = useAuth();
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = React.useState(false);

    return (
        <nav className="bg-govt-dark text-white shadow-md">
            <div className="max-w-7xl mx-auto px-4">
                {/* Main Header */}
                <div className="flex justify-between items-center h-20">
                    <Link to="/" className="flex items-center gap-3">
                        <div className="bg-white p-1 rounded">
                            <img src="/vite.svg" alt="Portal Logo" className="h-10 w-10" />
                        </div>
                        <div>
                            <h1 className="text-xl font-bold leading-none">College Clubs Information Portal</h1>
                            <p className="text-xs text-gray-300 uppercase mt-1 tracking-wider">Government of Student Affairs</p>
                        </div>
                    </Link>

                    {/* Desktop Nav */}
                    <div className="hidden md:flex items-center gap-8">
                        <Link to="/directory" className="hover:text-govt-accent transition-colors font-medium">Clubs</Link>
                        <Link to="/notices" className="hover:text-govt-accent transition-colors font-medium">Notices</Link>
                        <Link to="/events" className="hover:text-govt-accent transition-colors font-medium">Events</Link>

                        <div className="flex items-center gap-4 ml-4">
                            {user ? (
                                <div className="flex items-center gap-4">
                                    <Link to={`/dashboard/${role}`} className="flex items-center gap-2 bg-govt-blue px-4 py-2 rounded hover:bg-opacity-80 transition-all">
                                        <User size={18} />
                                        <span>Dashboard</span>
                                    </Link>
                                    <button onClick={() => signOut()} className="hover:text-red-400 transition-colors">
                                        <LogOut size={20} title="Logout" />
                                    </button>
                                </div>
                            ) : (
                                <div className="flex items-center gap-3">
                                    <Link to="/login" className="px-4 py-2 hover:underline">Login</Link>
                                    <Link to="/register-club" className="bg-govt-accent text-govt-dark font-bold px-4 py-2 rounded hover:brightness-110 transition-all">
                                        Register Club
                                    </Link>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Mobile Menu Button */}
                    <button className="md:hidden" onClick={() => setIsMenuOpen(!isMenuOpen)}>
                        {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
                    </button>
                </div>
            </div>

            {/* Mobile Menu */}
            {isMenuOpen && (
                <div className="md:hidden bg-govt-blue border-t border-govt-dark py-4 px-4 space-y-4">
                    <Link to="/directory" className="block py-2">Clubs</Link>
                    <Link to="/notices" className="block py-2">Notices</Link>
                    <Link to="/events" className="block py-2">Events</Link>
                    <hr className="border-govt-dark" />
                    {user ? (
                        <>
                            <Link to={`/dashboard/${role}`} className="block py-2">Dashboard</Link>
                            <button onClick={() => signOut()} className="block py-2 text-red-200">Logout</button>
                        </>
                    ) : (
                        <>
                            <Link to="/login" className="block py-2">Login</Link>
                            <Link to="/register-club" className="block py-2">Register Club</Link>
                        </>
                    )}
                </div>
            )}
        </nav>
    );
};

export default Navbar;

