import React, { useEffect, useState } from 'react';
import {
    Shield, CheckCircle, XCircle, Users,
    Search, ExternalLink, Mail, User, Clock,
    Trash2, Filter, ChevronRight, BarChart
} from 'lucide-react';
import { supabase } from '../../lib/supabase';

const AdminDashboard = () => {
    const [requests, setRequests] = useState<any[]>([]);
    const [clubs, setClubs] = useState<any[]>([]);
    const [activeTab, setActiveTab] = useState('requests');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        setLoading(true);
        // Fetch Pending Requests
        const { data: reqData } = await supabase
            .from('club_requests')
            .select('*')
            .order('created_at', { ascending: false });

        setRequests(reqData || []);

        // Fetch All Clubs
        const { data: clubData } = await supabase
            .from('clubs')
            .select('*')
            .order('created_at', { ascending: false });

        setClubs(clubData || []);
        setLoading(false);
    };

    const handleRequestAction = async (requestId: string, action: 'approved' | 'rejected') => {
        const { error } = await supabase
            .from('club_requests')
            .update({ status: action, reviewed_at: new Date().toISOString() })
            .eq('id', requestId);

        if (!error) {
            if (action === 'approved') {
                const req = requests.find(r => r.id === requestId);
                // In a real app, you'd trigger a function or another query to:
                // 1. Create the club entry
                // 2. Set the owner as club_admin
                // 3. Email the credentials
                console.log(`Logically creating club for: ${req.club_name}`);
            }
            fetchData();
        }
    };

    if (loading) return <div className="p-8 text-center">Loading Administrative Protocol...</div>;

    return (
        <div className="bg-gray-100 min-h-screen flex divide-x">
            {/* Admin Sidebar */}
            <aside className="w-64 bg-govt-dark text-white p-6 border-r flex flex-col gap-6">
                <div className="border-b border-white/10 pb-4">
                    <h3 className="font-bold flex items-center gap-2 uppercase tracking-widest text-sm">
                        <Shield size={20} className="text-govt-accent" />
                        Portal Moderation
                    </h3>
                    <p className="text-[10px] text-gray-400 mt-1">Institutional Level Access</p>
                </div>

                <nav className="flex flex-col gap-2">
                    {[
                        { id: 'requests', icon: <Clock size={18} />, label: 'Approval Queue', count: requests.filter(r => r.status === 'pending').length },
                        { id: 'clubs', icon: <Users size={18} />, label: 'Active Clubs', count: clubs.length },
                        { id: 'audit', icon: <BarChart size={18} />, label: 'Audit Logs', count: 0 },
                    ].map(item => (
                        <button
                            key={item.id}
                            onClick={() => setActiveTab(item.id)}
                            className={`flex items-center justify-between px-4 py-3 rounded text-sm transition-all ${activeTab === item.id ? 'bg-govt-accent text-govt-dark font-extrabold shadow-lg' : 'hover:bg-white/10'}`}
                        >
                            <span className="flex items-center gap-3">{item.icon} {item.label}</span>
                            {item.count > 0 && <span className="bg-white/20 px-2 py-0.5 rounded text-[10px]">{item.count}</span>}
                        </button>
                    ))}
                </nav>

                <div className="mt-auto pt-6 border-t border-white/10">
                    <button className="flex items-center gap-2 text-xs text-red-300 hover:underline">
                        <Shield size={14} /> Critical Override
                    </button>
                </div>
            </aside>

            {/* Main Content */}
            <main className="flex-grow p-10">
                <header className="flex justify-between items-center mb-10">
                    <div>
                        <h2 className="text-3xl font-extrabold text-govt-dark uppercase tracking-tight">Institutional Oversight</h2>
                        <p className="text-sm text-gray-500 font-bold uppercase tracking-widest mt-1">Authority Dashboard • Session 2026</p>
                    </div>
                    <div className="bg-white px-6 py-2 border rounded shadow-sm text-xs font-bold text-govt-dark">
                        Active Moderator: <span className="text-govt-blue">Root Administrator</span>
                    </div>
                </header>

                {activeTab === 'requests' && (
                    <div className="space-y-6">
                        <div className="grid grid-cols-1 gap-4">
                            {requests.length > 0 ? (
                                requests.map(req => (
                                    <div key={req.id} className="bg-white border-l-8 border-govt-accent p-6 shadow-sm flex flex-col md:row items-center justify-between gap-6 hover:shadow-md transition-shadow">
                                        <div className="md:w-3/5 space-y-2">
                                            <div className="flex items-center gap-3">
                                                <h4 className="text-lg font-bold text-govt-dark">{req.club_name}</h4>
                                                <span className={`text-[10px] font-bold px-2 py-0.5 rounded uppercase tracking-wider ${req.status === 'pending' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700'}`}>
                                                    {req.status}
                                                </span>
                                            </div>
                                            <p className="text-xs text-gray-600 line-clamp-2">{req.description}</p>
                                            <div className="flex flex-wrap gap-4 text-[10px] pt-2">
                                                <span className="flex items-center gap-1 text-gray-400 font-bold"><User size={10} /> REC: {req.contact_name}</span>
                                                <span className="flex items-center gap-1 text-gray-400 font-bold"><Mail size={10} /> {req.contact_email}</span>
                                            </div>
                                        </div>
                                        <div className="flex gap-3">
                                            {req.status === 'pending' ? (
                                                <>
                                                    <button
                                                        onClick={() => handleRequestAction(req.id, 'approved')}
                                                        className="flex items-center gap-2 bg-green-600 text-white px-6 py-2 rounded text-xs font-bold hover:bg-green-700 transition-colors"
                                                    >
                                                        <CheckCircle size={14} /> Approve
                                                    </button>
                                                    <button
                                                        onClick={() => handleRequestAction(req.id, 'rejected')}
                                                        className="flex items-center gap-2 border-2 border-red-500 text-red-500 px-6 py-2 rounded text-xs font-bold hover:bg-red-50 transition-colors"
                                                    >
                                                        <XCircle size={14} /> Reject
                                                    </button>
                                                </>
                                            ) : (
                                                <button className="text-xs text-govt-blue font-bold px-6 py-2 border rounded italic" disabled>
                                                    Record Finalized
                                                </button>
                                            )}
                                            <button className="p-2 border rounded hover:bg-gray-100">
                                                <ExternalLink size={16} />
                                            </button>
                                        </div>
                                    </div>
                                ))
                            ) : (
                                <div className="bg-white border p-20 text-center rounded text-gray-400 font-bold uppercase tracking-widest bg-[url('https://www.transparenttextures.com/patterns/cubes.png')] opacity-50">
                                    No Pending Vetting Applications
                                </div>
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'clubs' && (
                    <div className="space-y-6">
                        <div className="bg-white border p-4 shadow-sm flex items-center justify-between mb-2">
                            <div className="flex items-center gap-3 flex-grow">
                                <Search size={20} className="text-gray-400" />
                                <input type="text" placeholder="Filter active institutional repositories..." className="w-full outline-none text-sm font-medium" />
                            </div>
                            <button className="flex items-center gap-2 text-xs font-bold text-govt-blue">
                                <Filter size={14} /> Advance Filters
                            </button>
                        </div>

                        <div className="bg-white border rounded shadow-sm overflow-hidden">
                            <table className="w-full text-left">
                                <thead className="bg-govt-dark text-white text-[10px] font-bold uppercase tracking-widest">
                                    <tr>
                                        <th className="px-6 py-4">Repository Identity</th>
                                        <th className="px-6 py-4">Category</th>
                                        <th className="px-6 py-4">Status</th>
                                        <th className="px-6 py-4">Engagement</th>
                                        <th className="px-6 py-4 text-right">Moderation</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y text-sm">
                                    {clubs.map(club => (
                                        <tr key={club.id} className="hover:bg-gray-50 group">
                                            <td className="px-6 py-4">
                                                <div className="flex items-center gap-3">
                                                    <div className="h-8 w-8 bg-gray-100 rounded border flex items-center justify-center">
                                                        {club.logo_url ? <img src={club.logo_url} className="max-h-full" /> : <Users size={14} />}
                                                    </div>
                                                    <span className="font-bold text-govt-dark">{club.name}</span>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 text-xs font-bold text-gray-500 uppercase">{club.category}</td>
                                            <td className="px-6 py-4">
                                                {club.is_verified ? (
                                                    <span className="bg-green-50 text-green-700 text-[10px] font-bold px-2 py-0.5 rounded border border-green-200">VERIFIED</span>
                                                ) : (
                                                    <span className="bg-gray-100 text-gray-500 text-[10px] font-bold px-2 py-0.5 rounded border">VETTING</span>
                                                )}
                                            </td>
                                            <td className="px-6 py-4 text-xs text-govt-blue font-bold">{club.website_clicks} CLICKS</td>
                                            <td className="px-6 py-4 text-right space-x-2 opacity-0 group-hover:opacity-100 transition-opacity">
                                                <button className="p-1.5 text-govt-blue hover:scale-110 active:scale-95"><Shield size={16} /></button>
                                                <button className="p-1.5 text-red-500 hover:scale-110 active:scale-95"><Trash2 size={16} /></button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
};

export default AdminDashboard;

