source:
import React, { useEffect, useState } from 'react';
import {
    BarChart, Users, Bell, Calendar,
    Settings, LogOut, Plus, Edit, Trash2,
    CheckCircle, Clock, Upload, Globe, User
} from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import { supabase } from '../../lib/supabase';

const ClubDashboard = () => {
    const { profile, signOut } = useAuth();
    const [club, setClub] = useState<any>(null);
    const [updates, setUpdates] = useState<any[]>([]);
    const [events, setEvents] = useState<any[]>([]);
    const [stats, setStats] = useState({ clicks: 0, updates: 0, events: 0 });
    const [activeTab, setActiveTab] = useState('overview');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (profile) {
            fetchClubData();
        }
    }, [profile]);

    const fetchClubData = async () => {
        // 1. Get the club where this user is admin
        const { data: memberData } = await supabase
            .from('club_members')
            .select('club_id')
            .eq('user_id', profile.user_id)
            .eq('is_admin', true)
            .single();

        if (memberData) {
            const { data: clubData } = await supabase
                .from('clubs')
                .select('*')
                .eq('id', memberData.club_id)
                .single();

            setClub(clubData);

            if (clubData) {
                // Fetch Updates
                const { data: upData } = await supabase
                    .from('updates')
                    .select('*')
                    .eq('club_id', clubData.id)
                    .order('created_at', { ascending: false });

                setUpdates(upData || []);

                // Fetch Events
                const { data: evData } = await supabase
                    .from('events')
                    .select('*')
                    .eq('club_id', clubData.id)
                    .order('start_time', { ascending: true });

                setEvents(evData || []);

                setStats({
                    clicks: Number(clubData.website_clicks) || 0,
                    updates: upData?.length || 0,
                    events: evData?.length || 0
                });
            }
        }
        setLoading(false);
    };

    if (loading) return <div className="p-8 text-center">Loading Admin Console...</div>;
    if (!club) return (
        <div className="p-8 text-center bg-white border m-8 shadow shadow-red-100">
            <h2 className="text-xl font-bold text-red-600">Access Denied / No Club Assigned</h2>
            <p className="mt-2 text-gray-500">Your profile is not currently linked to any managed club repository.</p>
            <button onClick={() => signOut()} className="mt-4 text-govt-blue font-bold flex items-center justify-center mx-auto gap-2">
                <LogOut size={18} /> Logout Session
            </button>
        </div>
    );

    return (
        <div className="bg-gray-50 min-h-screen flex flex-col md:flex-row divide-x">
            {/* Admin Sidebar */}
            <aside className="w-full md:w-64 bg-white border-r min-h-[50vh] md:min-h-screen">
                <div className="p-6 border-b bg-govt-dark text-white">
                    <h3 className="font-bold flex items-center gap-2">
                        <Settings size={20} className="text-govt-accent" />
                        Management console
                    </h3>
                    <p className="text-[10px] uppercase mt-2 tracking-widest text-govt-accent font-bold">{club.name}</p>
                </div>
                <nav className="p-4 space-y-1">
                    {[
                        { id: 'overview', icon: <BarChart size={18} />, label: 'Dashboard Overview' },
                        { id: 'profile', icon: <User size={18} />, label: 'Club Repository' },
                        { id: 'updates', icon: <Bell size={18} />, label: 'Notices & Feed' },
                        { id: 'events', icon: <Calendar size={18} />, label: 'Calendar Events' },
                    ].map(item => (
                        <button
                            key={item.id}
                            onClick={() => setActiveTab(item.id)}
                            className={`w-full flex items-center gap-3 px-4 py-3 rounded text-sm transition-colors ${activeTab === item.id ? 'bg-govt-blue text-white font-bold' : 'text-gray-600 hover:bg-gray-100'}`}
                        >
                            {item.icon} {item.label}
                        </button>
                    ))}
                </nav>
            </aside>

            {/* Main Content Area */}
            <main className="flex-grow p-8">
                <header className="flex justify-between items-center mb-10 pb-4 border-b">
                    <div>
                        <h2 className="text-2xl font-bold text-govt-dark capitalize">{activeTab} Management</h2>
                        <p className="text-xs text-gray-500 uppercase tracking-widest font-bold">Session 2026 • Administrative Mode</p>
                    </div>
                    <button onClick={() => signOut()} className="text-xs font-bold text-red-500 hover:underline flex items-center gap-1">
                        <LogOut size={16} /> Termination Session
                    </button>
                </header>

                {activeTab === 'overview' && (
                    <div className="space-y-8 animate-in fade-in slide-in-from-bottom-2 duration-500">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                            <div className="bg-white border p-6 rounded shadow-sm text-center">
                                <div className="text-4xl font-black text-govt-dark">{stats.clicks}</div>
                                <div className="text-[10px] font-bold text-gray-400 uppercase mt-1">Repository Reach (Clicks)</div>
                            </div>
                            <div className="bg-white border p-6 rounded shadow-sm text-center">
                                <div className="text-4xl font-black text-govt-dark">{stats.updates}</div>
                                <div className="text-[10px] font-bold text-gray-400 uppercase mt-1">Active Notifications</div>
                            </div>
                            <div className="bg-white border p-6 rounded shadow-sm text-center">
                                <div className="text-4xl font-black text-govt-dark">{stats.events}</div>
                                <div className="text-[10px] font-bold text-gray-400 uppercase mt-1">Upcoming Events</div>
                            </div>
                        </div>

                        <div className="bg-white border overflow-hidden rounded shadow-sm">
                            <div className="px-6 py-4 bg-gray-50 border-b flex justify-between items-center">
                                <h4 className="font-bold text-sm uppercase tracking-tight">Recent Update Feed</h4>
                                <button onClick={() => setActiveTab('updates')} className="text-xs text-govt-blue font-bold hover:underline">Manage All</button>
                            </div>
                            <div className="divide-y">
                                {updates.slice(0, 5).map(up => (
                                    <div key={up.id} className="p-4 flex justify-between items-center">
                                        <div className="flex items-center gap-3">
                                            <div className={`w-2 h-2 rounded-full ${up.is_published ? 'bg-green-500' : 'bg-gray-300'}`}></div>
                                            <span className="text-sm font-medium text-govt-dark">{up.title}</span>
                                        </div>
                                        <span className="text-[10px] text-gray-400">{new Date(up.published_at).toLocaleDateString()}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}

                {(activeTab === 'updates' || activeTab === 'events') && (
                    <div className="space-y-6">
                        <div className="bg-white border p-12 text-center rounded-sm space-y-4">
                            <div className="flex justify-center"><Plus size={48} className="text-gray-200" /></div>
                            <div>
                                <h4 className="font-bold text-govt-dark underline underline-offset-4 decoration- govt-accent capitalize">Create New {activeTab.slice(0, -1)}</h4>
                                <p className="text-xs text-gray-400 mt-2 max-w-sm mx-auto">Standard government publishing format required. Author identification will be logged for audit trails.</p>
                            </div>
                            <button className="bg-govt-dark text-white px-8 py-2 rounded text-sm font-bold shadow hover:scale-105 transition-transform">
                                Initialize Form
                            </button>
                        </div>

                        <div className="bg-white border rounded">
                            <table className="w-full text-left">
                                <thead className="bg-govt-blue text-white text-[10px] font-bold uppercase tracking-wider">
                                    <tr>
                                        <th className="px-6 py-3">Title / Identification</th>
                                        <th className="px-6 py-3">Mode</th>
                                        <th className="px-6 py-3">Timestamp</th>
                                        <th className="px-6 py-3 text-right">Actions</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y text-sm">
                                    {(activeTab === 'updates' ? updates : events).map(item => (
                                        <tr key={item.id} className="group hover:bg-gray-50">
                                            <td className="px-6 py-4">
                                                <div className="font-bold text-govt-dark">{item.title}</div>
                                                <div className="text-[10px] text-gray-400">ID: {item.id.slice(0, 8)}...</div>
                                            </td>
                                            <td className="px-6 py-4">
                                                <span className={`px-2 py-0.5 rounded text-[10px] font-bold ${item.is_published ? 'bg-green-50 text-green-700 border border-green-200' : 'bg-gray-100 text-gray-600 border border-gray-200'}`}>
                                                    {item.is_published ? 'PUBLISHED' : 'DRAFT'}
                                                </span>
                                            </td>
                                            <td className="px-6 py-4 text-xs text-gray-500">
                                                {new Date(item.created_at || item.start_time).toLocaleDateString()}
                                            </td>
                                            <td className="px-6 py-4 text-right space-x-3">
                                                <button className="text-govt-blue hover:scale-110 transition-transform"><Edit size={16} /></button>
                                                <button className="text-red-400 hover:scale-110 transition-transform"><Trash2 size={16} /></button>
                                            </td>
                                        </tr>
                                    ))}
                                    {((activeTab === 'updates' ? updates : events).length === 0) && (
                                        <tr>
                                            <td colSpan={4} className="px-6 py-12 text-center text-gray-400 italic">No historical records available in this directory.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}

                {activeTab === 'profile' && (
                    <div className="bg-white border shadow-sm rounded-sm">
                        <div className="p-8 space-y-10">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                                <div className="space-y-6">
                                    <div className="flex flex-col items-center p-6 border-2 border-dashed rounded-sm bg-gray-50 group cursor-pointer">
                                        {club.logo_url ? <img src={club.logo_url} className="h-32 w-32 object-contain" /> : <Upload size={40} className="text-gray-300" />}
                                        <span className="text-[10px] font-bold mt-4 text-govt-blue uppercase tracking-widest group-hover:underline">Update Institutional Logo</span>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Repository Name</label>
                                        <input type="text" readOnly value={club.name} className="w-full bg-gray-100 border p-3 rounded text-sm text-gray-600 px-4" />
                                        <span className="text-[8px] text-gray-400 italic underline">Name changes require Moderator authorization via official request.</span>
                                    </div>
                                </div>
                                <div className="space-y-6">
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Public Redirect URL</label>
                                        <div className="flex gap-2">
                                            <div className="bg-gray-100 px-4 py-3 rounded border text-sm text-gray-400 flex items-center gap-2 flex-grow">
                                                <Globe size={14} /> portal.edu/club/{club.slug}
                                            </div>
                                            <Link to={`/club/${club.slug}`} target="_blank" className="bg-govt-dark text-white p-3 rounded hover:bg-govt-blue"><ExternalLink size={18} /></Link>
                                        </div>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Short Memo (Description)</label>
                                        <textarea rows={4} className="w-full border-2 p-4 rounded text-sm focus:border-govt-blue outline-none" defaultValue={club.short_description}></textarea>
                                    </div>
                                </div>
                            </div>
                            <div className="pt-8 border-t flex justify-end">
                                <button className="bg-govt-dark text-white px-12 py-3 rounded font-bold uppercase tracking-widest shadow-lg hover:brightness-110 active:scale-95 transition-all">
                                    Sync Repository
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
};

export default ClubDashboard;

