import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Search, Filter, Users, ChevronRight, CheckCircle } from 'lucide-react';
import { supabase } from '../lib/supabase';

const Directory = () => {
    const [clubs, setClubs] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [categories, setCategories] = useState<any[]>([]);

    useEffect(() => {
        fetchCategories();
        fetchClubs();
    }, []);

    const fetchCategories = async () => {
        const { data } = await supabase.from('categories').select('*');
        if (data) setCategories(data);
    };

    const fetchClubs = async () => {
        let query = supabase.from('clubs').select('*').eq('is_active', true);
        const { data } = await query;
        if (data) setClubs(data);
        setLoading(false);
    };

    const filteredClubs = clubs.filter(club => {
        const matchesSearch = club.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            club.short_description?.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesCategory = selectedCategory === 'All' || club.category === selectedCategory;
        return matchesSearch && matchesCategory;
    });

    return (
        <div className="bg-govt-light min-h-[80vh] pb-20">
            {/* Header */}
            <div className="bg-white border-b py-8 shadow-sm">
                <div className="max-w-7xl mx-auto px-4">
                    <nav className="text-xs text-gray-500 mb-2 flex items-center gap-1">
                        <Link to="/" className="hover:underline">Home</Link>
                        <ChevronRight size={12} />
                        <span className="text-govt-dark font-bold">Clubs Directory</span>
                    </nav>
                    <h2 className="text-3xl font-bold text-govt-dark">Clubs & Student Chapters</h2>
                    <p className="text-gray-600 mt-1">Discover various student-run organizations at College</p>
                </div>
            </div>

            <div className="max-w-7xl mx-auto px-4 mt-8">
                <div className="flex flex-col lg:flex-row gap-8">
                    {/* Filters Sidebar */}
                    <div className="lg:w-1/4 space-y-6">
                        <div className="bg-white border p-6 rounded shadow-sm">
                            <h3 className="font-bold text-govt-dark border-b pb-2 mb-4 flex items-center gap-2">
                                <Filter size={18} className="text-govt-accent" /> Filter By
                            </h3>

                            <div className="space-y-4">
                                <div>
                                    <label className="text-xs font-bold text-gray-500 uppercase tracking-wider block mb-2">Category</label>
                                    <select
                                        value={selectedCategory}
                                        onChange={(e) => setSelectedCategory(e.target.value)}
                                        className="w-full border rounded px-3 py-2 text-sm outline-govt-blue"
                                    >
                                        <option value="All">All Categories</option>
                                        {categories.map(cat => (
                                            <option key={cat.id} value={cat.label}>{cat.label}</option>
                                        ))}
                                        {/* Fallback categories if table is empty */}
                                        {!categories.length && [
                                            'Art & Culture', 'Technical', 'Drama', 'Sports', 'Photography', 'Music', 'Coding'
                                        ].map(cat => (
                                            <option key={cat} value={cat}>{cat}</option>
                                        ))}
                                    </select>
                                </div>

                                <div>
                                    <label className="text-xs font-bold text-gray-500 uppercase tracking-wider block mb-2">Sort By</label>
                                    <select className="w-full border rounded px-3 py-2 text-sm outline-govt-blue">
                                        <option>Alphabetical (A-Z)</option>
                                        <option>Recently Added</option>
                                        <option>Most Active</option>
                                    </select>
                                </div>
                            </div>

                            <div className="mt-8 pt-6 border-t">
                                <div className="bg-govt-light p-4 rounded text-xs text-gray-600">
                                    <p className="font-bold text-govt-dark mb-1">Join a Club!</p>
                                    Check eligibility and membership requirements on individual club pages.
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Directory Content */}
                    <div className="lg:w-3/4 space-y-6">
                        {/* Search Bar */}
                        <div className="bg-white border p-4 shadow-sm flex items-center gap-3">
                            <Search className="text-gray-400" />
                            <input
                                type="text"
                                placeholder="Search by club name or keyword..."
                                className="flex-grow outline-none border-b focus:border-govt-blue transition-colors py-1 text-sm"
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                            />
                            <span className="text-xs text-gray-400 whitespace-nowrap">
                                Showing {filteredClubs.length} results
                            </span>
                        </div>

                        {/* Clubs Grid/Table Loop */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            {loading ? (
                                [1, 2, 3, 4].map(i => <div key={i} className="h-48 bg-gray-100 animate-pulse rounded"></div>)
                            ) : filteredClubs.length > 0 ? (
                                filteredClubs.map(club => (
                                    <div key={club.id} className="bg-white border hover:shadow-md transition-all group overflow-hidden flex flex-col">
                                        <div className="p-6 flex gap-5">
                                            <div className="h-20 w-20 bg-gray-50 border rounded-sm flex-shrink-0 flex items-center justify-center p-2 relative">
                                                {club.logo_url ? (
                                                    <img src={club.logo_url} alt={club.name} className="max-h-full max-w-full object-contain" />
                                                ) : (
                                                    <Users size={32} className="text-gray-300" />
                                                )}
                                                {club.is_verified && (
                                                    <div className="absolute -top-2 -right-2 bg-white rounded-full">
                                                        <CheckCircle size={18} className="text-green-600 fill-white" />
                                                    </div>
                                                )}
                                            </div>
                                            <div>
                                                <span className="text-[10px] text-govt-blue font-bold uppercase tracking-widest">{club.category}</span>
                                                <h3 className="font-bold text-govt-dark text-lg group-hover:text-govt-blue transition-colors">
                                                    {club.name}
                                                </h3>
                                                <p className="text-xs text-gray-600 mt-2 line-clamp-2">
                                                    {club.short_description || "No description provided for this club repository."}
                                                </p>
                                            </div>
                                        </div>
                                        <div className="mt-auto border-t p-3 bg-gray-50 flex justify-between items-center px-6">
                                            <div className="flex gap-4">
                                                <span className="text-[10px] flex items-center gap-1 text-gray-500">
                                                    <Users size={12} /> Registered
                                                </span>
                                            </div>
                                            <Link to={`/club/${club.slug}`} className="text-xs font-bold text-govt-dark hover:text-govt-accent flex items-center gap-1">
                                                View Profile <ChevronRight size={14} />
                                            </Link>
                                        </div>
                                    </div>
                                ))
                            ) : (
                                <div className="col-span-full py-20 text-center bg-white border rounded shadow-inner">
                                    <p className="text-gray-500 italic">No clubs found matching your search criteria.</p>
                                    <button
                                        onClick={() => { setSearchTerm(''); setSelectedCategory('All'); }}
                                        className="text-govt-blue text-sm font-bold mt-2 hover:underline"
                                    >
                                        Clear all filters
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Directory;

import React, { useEffect, useState } from 'react';
import { Calendar, Search, MapPin, Globe, ExternalLink, ChevronRight, Clock } from 'lucide-react';
import { supabase } from '../lib/supabase';
import { Link } from 'react-router-dom';

const EventsPage = () => {
    const [events, setEvents] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchEvents = async () => {
            const { data } = await supabase
                .from('events')
                .select('*, clubs(name, slug)')
                .eq('is_published', true)
                .order('start_time', { ascending: true });

            setEvents(data || []);
            setLoading(false);
        };
        fetchEvents();
    }, []);

    return (
        <div className="bg-govt-light min-h-screen pb-20">
            <div className="bg-govt-dark text-white py-12 px-4 shadow-inner">
                <div className="max-w-7xl mx-auto flex flex-col md:row items-center justify-between gap-8">
                    <div className="md:w-2/3">
                        <h2 className="text-4xl font-black uppercase tracking-tight">Institutional Events Calendar</h2>
                        <p className="mt-2 text-gray-400">Centrally managed schedule for all student-led events and chapters</p>
                    </div>
                    <div className="md:w-1/3 flex justify-end">
                        <div className="bg-govt-blue/50 p-6 rounded border border-white/10 text-center">
                            <div className="text-3xl font-bold">{events.length}</div>
                            <div className="text-[10px] uppercase font-bold tracking-widest text-govt-accent">Active Events Tracked</div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="max-w-7xl mx-auto px-4 mt-12">
                {loading ? (
                    <div className="p-20 text-center bg-white border animate-pulse rounded">Synchronizing with Official Calendar...</div>
                ) : events.length > 0 ? (
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                        {events.map((event) => (
                            <div key={event.id} className="bg-white border hover:shadow-xl transition-all group rounded-sm overflow-hidden flex flex-col md:row">
                                {/* Date Side Panel */}
                                <div className="md:w-32 bg-gray-50 border-r flex flex-col items-center justify-center p-6 gap-1 group-hover:bg-govt-blue group-hover:text-white transition-colors">
                                    <span className="text-xs font-bold uppercase tracking-widest">{new Date(event.start_time).toLocaleString('default', { month: 'short' })}</span>
                                    <span className="text-4xl font-black leading-none">{new Date(event.start_time).getDate()}</span>
                                    <span className="text-[10px] font-medium opacity-60">{new Date(event.start_time).getFullYear()}</span>
                                </div>

                                {/* Event Details */}
                                <div className="flex-grow p-8 space-y-4">
                                    <div className="flex justify-between items-center">
                                        <Link to={`/club/${event.clubs?.slug}`} className="text-[10px] font-bold text-govt-blue uppercase tracking-widest hover:underline">
                                            Hosted by: {event.clubs?.name}
                                        </Link>
                                        <span className="text-[10px] bg-red-50 text-red-600 px-2 py-0.5 rounded font-bold border border-red-100">UPCOMING</span>
                                    </div>
                                    <h3 className="text-xl font-extrabold text-govt-dark leading-tight group-hover:underline cursor-pointer">
                                        {event.title}
                                    </h3>
                                    <div className="flex flex-wrap gap-6 text-xs text-gray-500">
                                        <span className="flex items-center gap-1.5 font-medium"><Clock size={14} className="text-govt-accent" /> {new Date(event.start_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} Onwards</span>
                                        <span className="flex items-center gap-1.5 font-medium">
                                            {event.is_online ? <Globe size={14} className="text-govt-blue" /> : <MapPin size={14} className="text-govt-blue" />}
                                            {event.location || "Location TBD"}
                                        </span>
                                    </div>
                                    <p className="text-sm text-gray-600 line-clamp-2 leading-relaxed">
                                        {event.description || "Official institutional event coordinated by students chapters. Click for more details."}
                                    </p>
                                    <div className="pt-2 flex justify-between items-center">
                                        <button className="text-xs font-bold text-govt-dark flex items-center gap-1 hover:text-govt-accent">
                                            Full Protocol <ChevronRight size={14} />
                                        </button>
                                        {event.registration_url && (
                                            <a href={event.registration_url} className="bg-govt-dark text-white px-6 py-2 rounded text-xs font-bold shadow-md hover:bg-govt-blue transition-all uppercase tracking-widest">
                                                Register Now
                                            </a>
                                        )}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                ) : (
                    <div className="bg-white border p-32 text-center rounded text-gray-400 capitalize bg-[url('https://www.transparenttextures.com/patterns/grid-me.png')]">
                        Historical data only. No upcoming events scheduled in the official roster.
                    </div>
                )}
            </div>

            {/* Guidelines Section */}
            <div className="max-w-7xl mx-auto px-4 mt-20">
                <div className="bg-white border p-12 rounded-sm shadow-sm flex flex-col md:row items-center gap-12">
                    <div className="md:w-2/3 space-y-4">
                        <h4 className="text-2xl font-bold text-govt-dark uppercase tracking-tight">Organizing Institutional Events</h4>
                        <p className="text-gray-600">
                            All events listed here have undergone institutional vetting by the Students Affairs Division.
                            To host an event, club admins must submit a formal protocol request through their dashboard at least 15 days in advance.
                        </p>
                        <button className="text-govt-blue font-bold text-sm flex items-center gap-1 underline">
                            <ExternalLink size={16} /> Read Event Authorization Policy
                        </button>
                    </div>
                    <div className="md:w-1/3 flex justify-center">
                        <div className="h-40 w-40 border-8 border-govt-light rounded-full flex items-center justify-center bg-gray-50 text-govt-dark relative">
                            <Calendar size={64} className="opacity-10" />
                            <div className="absolute inset-0 flex items-center justify-center font-black text-4xl">SAC</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EventsPage;

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Lock, Mail, ChevronRight, LogIn } from 'lucide-react';
import { supabase } from '../lib/supabase';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const navigate = useNavigate();

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        const { data, error } = await supabase.auth.signInWithPassword({
            email,
            password,
        });

        if (error) {
            setError(error.message);
            setLoading(false);
        } else {
            // Fetch profile to redirect correctly
            const { data: profile } = await supabase
                .from('profiles')
                .select('*, roles(slug)')
                .eq('user_id', data.user?.id)
                .single();

            const role = profile?.roles?.slug || 'student';
            navigate(`/dashboard/${role}`);
        }
    };

    return (
        <div className="bg-govt-light min-h-[80vh] flex items-center justify-center p-4">
            <div className="bg-white border-t-4 border-govt-dark shadow-xl max-w-md w-full p-8 md:p-12">
                <div className="text-center mb-10">
                    <div className="bg-govt-dark inline-block p-4 rounded-full text-white mb-4">
                        <Lock size={32} />
                    </div>
                    <h2 className="text-2xl font-bold text-govt-dark uppercase tracking-tight">Portal Authentication</h2>
                    <p className="text-sm text-gray-500 mt-2 italic">Access for Club Admins and Portal Personnel</p>
                </div>

                {error && (
                    <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-6 text-sm text-red-700">
                        {error}
                    </div>
                )}

                <form onSubmit={handleLogin} className="space-y-6">
                    <div className="space-y-2">
                        <label className="text-xs font-bold text-govt-dark uppercase tracking-widest flex items-center gap-2">
                            <Mail size={14} /> Registered Email
                        </label>
                        <input
                            type="email"
                            required
                            placeholder="admin@college.edu.in"
                            className="w-full border-2 border-gray-200 focus:border-govt-blue outline-none px-4 py-3 transition-colors rounded-sm"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>

                    <div className="space-y-2">
                        <label className="text-xs font-bold text-govt-dark uppercase tracking-widest flex items-center gap-2">
                            <Lock size={14} /> Security Key
                        </label>
                        <input
                            type="password"
                            required
                            placeholder="••••••••"
                            className="w-full border-2 border-gray-200 focus:border-govt-blue outline-none px-4 py-3 transition-colors rounded-sm"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-govt-dark text-white font-bold py-4 rounded-sm hover:bg-govt-blue transition-all uppercase tracking-widest flex items-center justify-center gap-3"
                    >
                        {loading ? 'Authenticating...' : (
                            <>
                                <LogIn size={20} /> Authorize Access
                            </>
                        )}
                    </button>
                </form>

                <div className="mt-8 pt-8 border-t text-center space-y-4">
                    <p className="text-xs text-gray-500">
                        Unauthorized access to this portal is strictly prohibited and subject to institutional policies.
                    </p>
                    <button onClick={() => navigate('/')} className="text-govt-blue text-xs font-bold hover:underline">
                        Return to Public Portal
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Login;

import React, { useEffect, useState } from 'react';
import { Bell, Search, Filter, ChevronRight, ExternalLink } from 'lucide-react';
import { supabase } from '../lib/supabase';
import { Link } from 'react-router-dom';

const NoticeBoardPage = () => {
    const [notices, setNotices] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [filter, setFilter] = useState('All');

    useEffect(() => {
        const fetchNotices = async () => {
            const { data } = await supabase
                .from('updates')
                .select('*, clubs(name, slug)')
                .eq('is_published', true)
                .order('published_at', { ascending: false });

            setNotices(data || []);
            setLoading(false);
        };
        fetchNotices();
    }, []);

    const filteredNotices = filter === 'All' ? notices : notices.filter(n => n.type === filter);

    return (
        <div className="bg-govt-light min-h-screen pb-20">
            <div className="bg-white border-b py-8 shadow-sm">
                <div className="max-w-7xl mx-auto px-4">
                    <h2 className="text-3xl font-bold text-govt-dark flex items-center gap-3">
                        <Bell className="text-govt-accent" />
                        Institutional Notice Board
                    </h2>
                    <p className="text-gray-600 mt-1">Official updates and announcements from all recognized clubs</p>
                </div>
            </div>

            <div className="max-w-7xl mx-auto px-4 mt-8 flex flex-col md:row gap-8">
                {/* Filter Sidebar */}
                <aside className="md:w-1/4 space-y-4">
                    <div className="bg-white border p-6 rounded shadow-sm">
                        <h4 className="font-bold text-govt-dark uppercase text-xs tracking-widest border-b pb-2 mb-4">Notice Type</h4>
                        <div className="space-y-2">
                            {['All', 'Notice', 'Announcement', 'Event', 'Urgent'].map(type => (
                                <button
                                    key={type}
                                    onClick={() => setFilter(type)}
                                    className={`w-full text-left px-3 py-2 rounded text-sm transition-all ${filter === type ? 'bg-govt-blue text-white font-bold' : 'hover:bg-gray-100 text-gray-600'}`}
                                >
                                    {type}
                                </button>
                            ))}
                        </div>
                    </div>
                </aside>

                {/* Notices Main List */}
                <div className="md:w-3/4 space-y-4">
                    {loading ? (
                        <div className="p-20 text-center animate-pulse">Scanning Archive...</div>
                    ) : filteredNotices.length > 0 ? (
                        filteredNotices.map(notice => (
                            <div key={notice.id} className="bg-white border p-6 shadow-sm hover:shadow-md transition-shadow group flex items-start gap-6">
                                <div className="flex flex-col items-center justify-center bg-gray-50 border rounded p-3 min-w-[80px]">
                                    <span className="text-[10px] uppercase font-bold text-govt-blue">{new Date(notice.published_at).toLocaleString('default', { month: 'short' })}</span>
                                    <span className="text-2xl font-black text-govt-dark">{new Date(notice.published_at).getDate()}</span>
                                </div>
                                <div className="flex-grow">
                                    <div className="flex justify-between items-start">
                                        <span className="text-[10px] font-bold text-govt-accent uppercase tracking-widest">{notice.type}</span>
                                        <Link to={`/club/${notice.clubs?.slug}`} className="text-xs text-govt-blue hover:underline font-bold flex items-center gap-1">
                                            {notice.clubs?.name} <ChevronRight size={12} />
                                        </Link>
                                    </div>
                                    <h3 className="text-lg font-bold text-govt-dark mt-1 group-hover:text-govt-blue transition-colors">{notice.title}</h3>
                                    <p className="text-sm text-gray-600 mt-2 line-clamp-3">{notice.body}</p>
                                    {notice.redirect_url && (
                                        <a href={notice.redirect_url} className="inline-flex items-center gap-1 text-xs font-bold text-govt-blue mt-4 hover:underline">
                                            View Document / Reference <ExternalLink size={14} />
                                        </a>
                                    )}
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="bg-white border p-20 text-center text-gray-400 italic rounded">
                            No active notifications found in this category.
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default NoticeBoardPage;

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    FileText, User, Mail, Link as LinkIcon,
    MapPin, Send, CheckCircle, ArrowRight, ArrowLeft
} from 'lucide-react';
import { supabase } from '../lib/supabase';

const RegisterClub = () => {
    const [step, setStep] = useState(1);
    const [loading, setLoading] = useState(false);
    const [submitted, setSubmitted] = useState(false);
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        club_name: '',
        category: '',
        description: '',
        website_url: '',
        contact_name: '',
        contact_email: '',
        social_facebook: '',
        social_instagram: '',
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);

        const { error } = await supabase
            .from('club_requests')
            .insert([
                {
                    ...formData,
                    status: 'pending'
                }
            ]);

        if (!error) {
            setSubmitted(true);
        } else {
            alert('Error submitting request: ' + error.message);
        }
        setLoading(false);
    };

    if (submitted) {
        return (
            <div className="bg-govt-light min-h-[80vh] flex items-center justify-center p-4">
                <div className="bg-white border-t-8 border-green-600 shadow-2xl max-w-2xl w-full p-12 text-center space-y-6">
                    <div className="flex justify-center">
                        <CheckCircle size={80} className="text-green-600" />
                    </div>
                    <h2 className="text-3xl font-bold text-govt-dark uppercase tracking-tight">Application Submitted Successfully</h2>
                    <p className="text-gray-600 leading-relaxed">
                        Your application for registering <strong>{formData.club_name}</strong> has been received by the Student Affairs Division.
                        A reference ID has been generated and sent to <strong>{formData.contact_email}</strong>.
                    </p>
                    <div className="bg-gray-50 p-6 border rounded text-sm text-left">
                        <p className="font-bold text-govt-dark mb-2 uppercase tracking-widest text-xs">Next Steps:</p>
                        <ul className="list-disc pl-5 space-y-2 text-gray-500">
                            <li>Verification of documents by the Moderator (2-3 working days)</li>
                            <li>Email notification for physical vetting (if required)</li>
                            <li>Provision of administrative credentials upon approval</li>
                        </ul>
                    </div>
                    <button
                        onClick={() => navigate('/')}
                        className="bg-govt-dark text-white font-bold px-8 py-3 rounded hover:bg-govt-blue transition-all"
                    >
                        Return to Home
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="bg-govt-light min-h-screen pb-20">
            {/* Header */}
            <div className="bg-govt-dark text-white py-12 px-4 shadow-inner">
                <div className="max-w-4xl mx-auto">
                    <h2 className="text-3xl font-bold uppercase tracking-tight border-l-4 border-govt-accent pl-6">New Club Registration Portal</h2>
                    <p className="mt-2 text-gray-400 pl-10">Official Onboarding for Institutional Recognition (Session 2026-27)</p>
                </div>
            </div>

            <div className="max-w-4xl mx-auto px-4 -mt-6">
                {/* Progress Indicator */}
                <div className="flex items-center gap-2 mb-8 bg-white p-4 shadow-sm border rounded">
                    {[1, 2, 3].map(i => (
                        <React.Fragment key={i}>
                            <div className={`h-8 w-8 rounded-full flex items-center justify-center font-bold text-sm ${step >= i ? 'bg-govt-dark text-white' : 'bg-gray-200 text-gray-500'}`}>
                                {i}
                            </div>
                            {i < 3 && <div className={`flex-grow h-1 ${step > i ? 'bg-govt-dark' : 'bg-gray-200'}`}></div>}
                        </React.Fragment>
                    ))}
                </div>

                <form onSubmit={handleSubmit} className="bg-white shadow-xl border rounded overflow-hidden">
                    <div className="p-8 md:p-12">
                        {step === 1 && (
                            <div className="space-y-6 animate-in fade-in duration-500">
                                <div className="border-b pb-4 mb-8">
                                    <h3 className="text-xl font-bold text-govt-dark flex items-center gap-2 uppercase tracking-tight">
                                        <FileText size={20} className="text-govt-accent" /> Section I: Basic Club Information
                                    </h3>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Club Official Name</label>
                                        <input
                                            type="text" name="club_name" required
                                            className="w-full border-b-2 border-gray-200 focus:border-govt-blue outline-none py-2 text-sm transition-all"
                                            placeholder="e.g. GECP Robotics Society"
                                            value={formData.club_name} onChange={handleChange}
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Category Type</label>
                                        <select
                                            name="category" required
                                            className="w-full border-b-2 border-gray-200 focus:border-govt-blue outline-none py-2 text-sm transition-all"
                                            value={formData.category} onChange={handleChange}
                                        >
                                            <option value="">Select Category</option>
                                            <option value="Technical">Technical</option>
                                            <option value="Art & Culture">Art & Culture</option>
                                            <option value="Drama">Drama</option>
                                            <option value="Sports">Sports</option>
                                            <option value="Photography">Photography</option>
                                        </select>
                                    </div>
                                    <div className="md:col-span-2 space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Short Description / Moto</label>
                                        <textarea
                                            name="description" required rows={3}
                                            className="w-full border-2 border-gray-100 p-3 rounded focus:border-govt-blue outline-none text-sm transition-all"
                                            placeholder="Briefly describe the club's primary objective..."
                                            value={formData.description} onChange={handleChange}
                                        />
                                    </div>
                                </div>
                            </div>
                        )}

                        {step === 2 && (
                            <div className="space-y-6 animate-in fade-in duration-500">
                                <div className="border-b pb-4 mb-8">
                                    <h3 className="text-xl font-bold text-govt-dark flex items-center gap-2 uppercase tracking-tight">
                                        <User size={20} className="text-govt-accent" /> Section II: Lead Representative Details
                                    </h3>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Contact Person Name</label>
                                        <input
                                            type="text" name="contact_name" required
                                            className="w-full border-b-2 border-gray-200 focus:border-govt-blue outline-none py-2 text-sm transition-all"
                                            placeholder="Full Name"
                                            value={formData.contact_name} onChange={handleChange}
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Institutional Email ID</label>
                                        <input
                                            type="email" name="contact_email" required
                                            className="w-full border-b-2 border-gray-200 focus:border-govt-blue outline-none py-2 text-sm transition-all"
                                            placeholder="name@college.edu.in"
                                            value={formData.contact_email} onChange={handleChange}
                                        />
                                    </div>
                                </div>
                            </div>
                        )}

                        {step === 3 && (
                            <div className="space-y-6 animate-in fade-in duration-500">
                                <div className="border-b pb-4 mb-8">
                                    <h3 className="text-xl font-bold text-govt-dark flex items-center gap-2 uppercase tracking-tight">
                                        <LinkIcon size={20} className="text-govt-accent" /> Section III: Digital Presence (Optional)
                                    </h3>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">External Website URL</label>
                                        <input
                                            type="url" name="website_url"
                                            className="w-full border-b-2 border-gray-200 focus:border-govt-blue outline-none py-2 text-sm transition-all"
                                            placeholder="https://yourclub.com"
                                            value={formData.website_url} onChange={handleChange}
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold text-gray-500 uppercase tracking-widest">Instagram Handle</label>
                                        <input
                                            type="text" name="social_instagram"
                                            className="w-full border-b-2 border-gray-200 focus:border-govt-blue outline-none py-2 text-sm transition-all"
                                            placeholder="@yourclub"
                                            value={formData.social_instagram} onChange={handleChange}
                                        />
                                    </div>
                                </div>

                                <div className="bg-yellow-50 p-4 border-l-4 border-yellow-400 text-xs text-yellow-800 space-y-2">
                                    <p className="font-bold flex items-center gap-1 uppercase tracking-tight">Declaration:</p>
                                    <p>I hereby declare that the information provided above is true to the best of my knowledge and complies with the Student Council Guidelines of 2026.</p>
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="bg-gray-50 p-8 border-t flex justify-between">
                        {step > 1 ? (
                            <button
                                type="button" onClick={() => setStep(step - 1)}
                                className="flex items-center gap-2 text-sm font-bold text-gray-500 hover:text-govt-dark py-2 px-6"
                            >
                                <ArrowLeft size={18} /> Back
                            </button>
                        ) : <div></div>}

                        {step < 3 ? (
                            <button
                                type="button" onClick={() => setStep(step + 1)}
                                className="flex items-center gap-2 bg-govt-dark text-white font-bold py-2 px-8 rounded hover:bg-govt-blue transition-all"
                            >
                                Continue <ArrowRight size={18} />
                            </button>
                        ) : (
                            <button
                                type="submit" disabled={loading}
                                className="flex items-center gap-2 bg-govt-accent text-govt-dark font-extrabold py-3 px-10 rounded shadow-lg hover: brightness-110 transition-all uppercase tracking-widest"
                            >
                                {loading ? 'Submitting Application...' : (
                                    <>
                                        <Send size={18} /> Submit Application
                                    </>
                                )}
                            </button>
                        )}
                    </div>
                </form>
            </div>
        </div>
    );
};

export default RegisterClub;
